计算机/工具/Vue/Vue组件.content:
基本
&lt;!DOCTYPE html&gt;
&lt;body&gt;
    &lt;div id=&quot;main-wrap&quot;&gt;
        &lt;div id=&quot;page-content&quot; class=&quot;header-static&quot;&gt;
            &lt;div class=&quot;container&quot;&gt;
                &lt;div class=&quot;row padding-onlytop-lg&quot;&gt;
                    &lt;div class=&quot;col-md-8 col-md-offset-2&quot;&gt;
                        &lt;h2 class=&quot;margin-bottom color left&quot;&gt;全局注册组件&lt;/h2&gt;
                        &lt;div id=&quot;testFather&quot;&gt;
                            &lt;component-father&gt;&lt;/component-father&gt;
                            &lt;component-father&gt;&lt;/component-father&gt;
                        &lt;/div&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
                &lt;script&gt;
                    /**
                     * 全局注册一个新组件
                    */
                    Vue.component(&#039;component-father&#039;, {
                        template: &#039;&lt;div&gt;&lt;span v-text=&quot;title&quot;&gt;&lt;/span&gt;&lt;/div&gt;&#039;,
                        //组件data必须是一个函数，否则所有组件公用同一个data实例
                        data: function () {
                            //组件返回的data是给它的template用的，理解为组件内部使用
                            return { title: &#039;儿子名字&#039; };
                        }
                    });
                    /**
                     * 开始使用组件
                     */
                    new Vue({
                        el: &#039;#testFather&#039;,
                        data: {

                        },
                        methods: {

                        }
                    })
                &lt;/script&gt;
                &lt;div class=&quot;row padding-onlytop-lg&quot;&gt;
                    &lt;div class=&quot;col-md-8 col-md-offset-2&quot;&gt;
                        &lt;h2 class=&quot;margin-bottom color left&quot;&gt;向组件中传入数据&lt;/h2&gt;
                        &lt;div id=&quot;testFather2&quot;&gt;
                            &lt;component-father-with-prop v-bind:name=&quot;name&quot; v-bind:age=&quot;age&quot;&gt;
                            &lt;/component-father-with-prop&gt;
                            &lt;!-- v-bind也可以不写属性名，效果和把componentData合并进组件的data一样 --&gt;
                            &lt;component-father-with-prop v-bind=&quot;componentData&quot;&gt;&lt;/component-father-with-prop&gt;
                        &lt;/div&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
                &lt;script&gt;
                    /**
                    * 向组件内传入数据
                    */
                    Vue.component(&#039;component-father-with-prop&#039;, {
                        //这里如果属性特别多可以直接写props:[&#039;option&#039;],把所有属性写入option
                        //然后模板里option.name获取
                        props: [&#039;name&#039;, &#039;age&#039;],
                        template: &#039;&lt;div&gt;&#039; +
                            &#039;&lt;span v-text=&quot;title&quot;&gt;&lt;/span&gt;&#039; +
                            &#039;&lt;span v-text=&quot;name&quot;&gt;&lt;/span&gt;&#039; +
                            &#039;&lt;span v-text=&quot;age&quot;&gt;&lt;/span&gt;&#039; +
                            &#039;&lt;/div&gt;&#039;,
                        data: function () {
                            return { title: &#039;儿子&#039; };
                        }
                    });
                    new Vue({
                        el: &#039;#testFather2&#039;,
                        data: {
                            componentData: {
                                name: &#039;儿子1&#039;, age: &#039;12岁&#039;
                            },
                            name: &#039;儿子2&#039;,
                            age: &#039;年龄12&#039;
                        },
                        methods: {

                        }
                    })
                &lt;/script&gt;
                &lt;div class=&quot;row padding-onlytop-lg&quot;&gt;
                    &lt;div class=&quot;col-md-8 col-md-offset-2&quot;&gt;
                        &lt;h2 class=&quot;margin-bottom color left&quot;&gt;监听子组件的事件&lt;/h2&gt;
                        &lt;div id=&quot;testFather3&quot;&gt;
                            &lt;component-father-with-event
                            v-bind:name=&quot;name&quot;
                            v-bind:age=&quot;age&quot;
                            @child-msg=&quot;forChild&quot;&gt;
                            &lt;/component-father-with-event&gt;
                        &lt;/div&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
                &lt;script&gt;
                    Vue.component(&#039;component-father-with-event&#039;, {
                        props: [&#039;name&#039;, &#039;age&#039;],
                        template: &#039;&lt;div&gt;&#039; +
                            /* 增加了一个点击事件直接更改prop请看changeProps方法 */
                            &#039;&lt;button @click=&quot;changeProps&quot;&gt;点我改变子组件属性值（会报错）&lt;/button&gt;&#039; +
                            &#039;&lt;/br&gt;&#039; +
                            &#039;&lt;button @click=&quot;changePropsRightWay&quot;&gt;点我改变父组件属性值（正确方法）&lt;/button&gt;&#039; +
                            &#039;&lt;/br&gt;&#039; +
                            &#039;&lt;span v-text=&quot;title&quot;&gt;&lt;/span&gt;&#039; +
                            &#039;&lt;span v-text=&quot;name&quot;&gt;&lt;/span&gt;&#039; +
                            &#039;&lt;span v-text=&quot;age&quot;&gt;&lt;/span&gt;&#039; +
                            &#039;&lt;/div&gt;&#039;,
                        data: function () {
