分出checkPhone和checkEmail

function check(reg){
	return function(str){
		return reg.test(str);
	}
}

function checkPhone(str){
	return check(/phonereg/);
}

function checkEmail(str){
	return check(/emailstr/);
}

//使用
checkPhone(&#039;str&#039;)
checkEmail(&#039;str&#039;)

